craft.script:
function main(){

    var headRadius = 5
    var headDepth = 1
    var depth = 10

    // needle
    var needle = cylinder({
        r: 1,
        h: depth                
    })

    // head
    var head = cylinder({
        r: headRadius,
        h: headDepth                
    })
    head = head.translate([0, 0, depth]);
    return union(head, needle).translate([headRadius,headRadius,0]);
}
