g.script:
function main(params, scope){
    var c = scope.solids[0]
    // new size
    expect(c).to.have.size(20,20,20)
    // same location
    expect(c).to.have.location(10,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    // new size
    expect(c).to.have.size(5,5,5)
    // same location
    expect(c).to.have.location(10,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    // new size
    expect(c).to.have.size(20,20,20)
    // same location
    expect(c).to.have.location(10,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    // only the size x is changed
    expect(c).to.have.size(20,20,30)
    // same location
    expect(c).to.have.location(10,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    // all the sizes are changed to 10
    expect(c).to.have.size(10,10,10)
    // same location
    expect(c).to.have.location(10,0,0)
}

craft.script:
function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(20,75,30)
    expect(c).to.have.location(10,0,0)
}
