g.script:
function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(20,30,40)
    expect(c).to.have.location(0,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    // size is scaled
    expect(c).to.have.size(20,30,40)
    // location is also scaled
    expect(c).to.have.location(20,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(20,30,40)
    expect(c).to.have.location(-20,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(10,30,30)
    expect(c).to.have.location(0,30,30)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(20,20,20)
    expect(c).to.have.location(20,20,20)
}
