g.script:
function main(params, scope){
    var c = scope.solids[0]
    // the size has increased
    expect(c).to.have.size(20,30,40)
    // still centered at the same place
    expect(c).to.have.center(5,5,5)
}

function main(params, scope){
    var c = scope.solids[0]
    // the size has increased
    expect(c).to.have.size(20,30,40)
    // still centered at the same place
    expect(c).to.have.center(15,5,5)
}

function main(params, scope){
    var c = scope.solids[0]
    // the size has increased
    expect(c).to.have.size(20,30,40)
    // still centered at the same place
    expect(c).to.have.center(-5,5,5)
}

function main(params, scope){
    var c = scope.solids[0]
    // the size has increased
    expect(c).to.have.size(20,20,20)
    // still centered at the same place
    expect(c).to.have.center(15,15,15)
}
