g.script:
function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(10,50,10)
    expect(c).to.have.location(20,-20,0)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(10,10,50)
    expect(c).to.have.location(20,0,-20)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(50,10,10)
    expect(c).to.have.location(0,0,0)
}
