craft.script:
function main(params, scope){

    var a = params.axis

    var contentSolids = scope.solids

    var tx = 0

    var solids = contentSolids.reverse()
    solids.forEach(function(solid){

        var d = {
            x: 0,
            y: 0,
            z: 0
        }

        d[a] = tx - solid.layout.location[a]
        solid.translate(d)

        tx = tx + solid.layout.size[a] + params.spacing
    })
}
