craft.script:
        function main(params, scope){

            var contentSolids = scope.solids[0].children

            var ty = 0
            var tz = 0
            _.forEachRight(contentSolids, function(solid){

//            contentSolids.reverse().forEach(function(solid){

                solid.translateTo({
                    x: solid.layout.location.x, // 2, // center along x
                    y: ty,
                    z: solid.layout.location.z //0   // place on x-y plane (z = 0)
                })

                ty = ty + solid.layout.size.y + params.spacing
            })

            scope.solids[0].fitToChildren1()

        }
