craft.script:
function main(params, scope){

    var contentSolids = scope.solids[0].children

    var tx = 0
    contentSolids.forEach(function(solid){

        solid.translateTo({
            x: tx,
            y: solid.layout.location.y, // 2, // center along y
            z: solid.layout.location.z //    // place on x-y plane (z = 0)
        })

        tx = tx + solid.layout.size.x + params.spacing
    })

    // var ymin = _.min(contentSolids.map(function(c) {
    //     return c.layout.location.y
    // }))

    // contentSolids.forEach(function(solid){
    //     solid.translate({x:0,y:-ymin,z:0})
    // })

    scope.solids[0].fitToChildren()

}
