craft.script:
function main() {           
    // handles fn
    if (params.sides > 100) { params.sides = 100; }

    var c, diff;

    // parts
    c = cylinder({
        r: params.width / 2,
        h: params.length,
        fn: params.sides + 1
    }).rotateY(90);

    diff = cube({
        size: [params.length, params.width, params.width]
    }).translate([0, -params.width / 2, -params.width]);

    return difference(c, diff).rotateZ(90);
}
