craft.script:
function main(params, scope) {
    var grp = scope.solids[0]
    var s
    if (params.factor != 1) {
        var f = params.factor
        s = {
            x: f,
            y: f,
            z: f
        }
    } else {
        var x = params.x
        var y = params.y
        var z = params.z
        s = {
            x: x,
            y: y,
            z: z
        }
    }
    grp.scale(s)
}
