g.script:
function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.location(100,0,0)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.location(0,100,0)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.location(0,0,100)
}

craft.script:
function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.size(110,110,110)
    expect(c).to.have.location(0,0,0)
}
