g.script:
function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.location(10,20,30)
}

function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.location(-10,-20,-30)
}

craft.script:
function main(params, scope){
    var c = scope.solids[0]
    expect(c).to.have.location(0,0,0)
}
